class RegisterEmployeeView(APIView, SignupView):
    """
    Accepts the credentials and creates a new user
    if user does not exist already
    Return the REST Token if the credentials are valid and authenticated.
    Calls allauth complete_signup method

    Accept the following POST parameters: username, email, password
    Return the REST Framework Token Object's key.
    """

    permission_classes = (AllowAny,)
    allowed_methods = ('POST', 'OPTIONS', 'HEAD')
    token_model = Token
    serializer_class = TokenSerializer

    def get(self, *args, **kwargs):
        return Response({}, status=status.HTTP_405_METHOD_NOT_ALLOWED)

    def put(self, *args, **kwargs):
        return Response({}, status=status.HTTP_405_METHOD_NOT_ALLOWED)

    def form_valid(self, form):
        # import ipdb; ipdb.set_trace();
        # set_user_role(self.user, roles.manager)
        self.user = form.save(self.request)
        self.token, created = self.token_model.objects.get_or_create(
            user=self.user
        )

        if isinstance(self.request, HttpRequest):
            request = self.request
        else:
            request = self.request._request
        return complete_signup(request, self.user,
                               app_settings.EMAIL_VERIFICATION,
                               self.get_success_url())



    def post(self, request, *args, **kwargs):
        self.initial = {}
        shop_name= self.request.data.pop('shopName')
        self.request.POST = self.request.data.copy()
        form_class = self.get_form_class()
        # import ipdb; ipdb.set_trace();
        # set_user_role(self.user, roles.manager)
        self.form = self.get_form(form_class)
        if self.form.is_valid():
            #mine
            self.user = self.form.save(self.request)
            self.user.save()
            set_user_role(self.user, roles.employee)
            org = Organization.objects.get(owner=self.request.user)
            shop = Shop(owner=org, employee = self.user, shopName=shop_name[0])
            shop.save()
            org.employee.add(self.user)
            org.save()
            return self.get_response()
        else:
            return self.get_response_with_errors()

    def update_csrf(self):
        # import ipdb; ipdb.set_trace();
        new_csrf = csrf.get_token(self.request)
        return new_csrf

    def get_response(self):

        # import ipdb; ipdb.set_t   race()
        # serializer = self.user_serializer_class(instance=self.user)
        # serializer = self.serializer_class(instance=self.token)
        username = self.request.data["username"]
        response = {"csrf_token":self.update_csrf()}
        return Response(response, status=status.HTTP_201_CREATED)

    def get_response_with_errors(self):
        # import ipdb; ipdb.set_trace()
        return Response(self.form.errors, status=status.HTTP_400_BAD_REQUEST)

    
class RegisterView(APIView, SignupView):
    """
    Accepts the credentials and creates a new user
    if user does not exist already
    Return the REST Token if the credentials are valid and authenticated.
    Calls allauth complete_signup method

    Accept the following POST parameters: username, email, password
    Return the REST Framework Token Object's key.
    """

    permission_classes = (AllowAny,)
    allowed_methods = ('POST', 'OPTIONS', 'HEAD')
    token_model = Token
    serializer_class = TokenSerializer

    def get(self, *args, **kwargs):
        return Response({}, status=status.HTTP_405_METHOD_NOT_ALLOWED)

    def put(self, *args, **kwargs):
        return Response({}, status=status.HTTP_405_METHOD_NOT_ALLOWED)

    def form_valid(self, form):
        # import ipdb; ipdb.set_trace();
        # set_user_role(self.user, roles.manager)
        self.user = form.save(self.request)
        set_user_role(self.user, roles.owner)
        self.token, created = self.token_model.objects.get_or_create(
            user=self.user
        )
        org = Organization(owner=self.user)
        org.save()
        if isinstance(self.request, HttpRequest):
            request = self.request
        else:
            request = self.request._request
        return complete_signup(request, self.user,
                               app_settings.EMAIL_VERIFICATION,
                               self.get_success_url())

    def post(self, request, *args, **kwargs):
        self.initial = {}
        self.request.POST = self.request.data.copy()
        form_class = self.get_form_class()
        # import ipdb; ipdb.set_trace();
        # set_user_role(self.user, roles.manager)
        self.form = self.get_form(form_class)
        if self.form.is_valid():
            self.form_valid(self.form)

            return self.get_response()
        else:
            return self.get_response_with_errors()

    def update_csrf(self):
        # import ipdb; ipdb.set_trace();
        new_csrf = csrf.get_token(self.request)
        return new_csrf

    def get_response(self):

        # import ipdb; ipdb.set_t   race()
        # serializer = self.user_serializer_class(instance=self.user)
        serializer = self.serializer_class(instance=self.token)
        username = self.request.data["username"]
        response = {"csrf_token":self.update_csrf()}
        return Response(response, status=status.HTTP_201_CREATED)

    def get_response_with_errors(self):
        # import ipdb; ipdb.set_trace()
        return Response(self.form.errors, status=status.HTTP_400_BAD_REQUEST)

